function createWriteBehindProxy() {
  const cache = new Map();
  const pendingWrites = new Set();

  setInterval(() => {
    pendingWrites.forEach(key => {
      localStorage.setItem(key, cache.get(key));
      pendingWrites.delete(key);
    });
  }, 1000);

  return new Proxy({}, {
    get: (_, key) => cache.get(key) || localStorage.getItem(key),
    set: (_, key, value) => {
      cache.set(key, value);
      pendingWrites.add(key);
      return true;
    }
  });
}

